// 定义武汉地区的边界
var wuhanBoundary = ee.FeatureCollection('users/your_username/WuhanBoundary');

// 加载NDVI数据
var ndviCollection = ee.ImageCollection([
  ee.Image('users/your_username/NDVImax2016'),
  ee.Image('users/your_username/NDVImax2017'),
  ee.Image('users/your_username/NDVImax2018'),
  ee.Image('users/your_username/NDVImax2019'),
  ee.Image('users/your_username/NDVImax2020')
]);

// 裁剪NDVI数据
var clippedNDVI = ndviCollection.map(function(image) {
  return image.clip(wuhanBoundary);
});

// 线性回归分析
var trend = clippedNDVI.reduce(ee.Reducer.linearFit());
var trendSlope = trend.select('scale');
var trendPval = trend.select('offset');

// 识别退化区域
var degraded = trendSlope.lt(0).and(trendPval.lt(0.05));

// 导出结果
Export.image.toDrive({
  image: degraded,
  description: 'degraded_areas_Wuhan',
  fileFormat: 'GeoTIFF',
  region: wuhanBoundary,
  scale: 30,
  maxPixels: 1e8
});
