// Load datasets
var busPoints = ee.FeatureCollection("users/your_username/bus_points_pro");
var roads = ee.FeatureCollection("users/your_username/Wuhanroad_pro");

// Create buffers
var buffer500 = busPoints.map(function(feature) {
  return feature.buffer(500);
});
var buffer1000 = busPoints.map(function(feature) {
  return feature.buffer(1000);
});

// Road intersection
var accessibleRoads = roads.filterBounds(buffer1000);

// Density analysis
var density = ee.Image().paint({
  featureCollection: busPoints,
  color: 1,
  width: 5
}).reduceToImage({
  reducer: ee.Reducer.count(),
  maxPixels: 1e8
});
var densityRaster = density.resample('bicubic').reproject({
  crs: 'EPSG:4326',
  scale: 100
});

// Overlay analysis
var extractedDensity = densityRaster.clip(accessibleRoads);

// Visualization
Map.centerObject(busPoints, 12);
Map.addLayer(buffer500, {color: 'red'}, '500m Buffer');
Map.addLayer(buffer1000, {color: 'blue'}, '1000m Buffer');
Map.addLayer(accessibleRoads, {color: 'green'}, 'Accessible Roads');
Map.addLayer(extractedDensity, {min: 0, max: 10, palette: ['white', 'blue']}, 'Extracted Density');
