// ================== Module 1: Environment Setup ==================
var wuhanRegion = ee.Geometry.Polygon([
  [[114.05, 30.4], [114.05, 30.7], [114.5, 30.7], [114.5, 30.4]]
]);

// ================== Module 2: Input Data ==================
var metro_lines = ee.FeatureCollection("users/yourusername/WuhanStation_roads_pro");
var bus_lines = ee.FeatureCollection("users/yourusername/WuhanBus_roads_pro");
var road_network = ee.FeatureCollection("users/yourusername/Wuhanroad_pro");

// ================== Module 3: Buffer Creation ==================
var metro_buffer = metro_lines.buffer(5);
var bus_buffer = bus_lines.buffer(5);
var road_buffer = road_network.buffer(5);

Map.centerObject(wuhanRegion, 12);
Map.addLayer(metro_buffer, {color: 'blue'}, 'Metro Buffer');
Map.addLayer(bus_buffer, {color: 'green'}, 'Bus Buffer');
Map.addLayer(road_buffer, {color: 'red'}, 'Road Buffer');

// ================== Module 4: Intersection Analysis ==================
var intersected_area = metro_buffer
  .intersection(bus_buffer)
  .intersection(road_buffer);

Map.addLayer(intersected_area, {color: 'purple'}, 'Intersected Area');

// ================== Module 5: Centroid Extraction ==================
var centroids = intersected_area.centroid();

Map.addLayer(centroids, {color: 'yellow'}, 'Centroids');

Export.table.toDrive({
  collection: centroids,
  description: 'centroids_export',
  fileFormat: 'GeoJSON'
});
