var pt = ee.Geometry.Point([35.51277232271257, 33.90045085313064]);
Map.setCenter(35.51272, 33.90051, 12);
var geometry = pt.buffer(4000);
Map.addLayer(geometry, {}, 'Buffer');
var collection = ee.ImageCollection('COPERNICUS/S5P/OFFL/L3_AER_AI').select('absorbing_aerosol_index').filterDate('2020-08-01', '2020-08-11');
Map.addLayer(collection.mean().clip(geometry), band_viz, 'S5P Aerosol');
var co = ee.ImageCollection('COPERNICUS/S5P/NRTI/L3_CO').select('CO_column_number_density').filterDate('2020-08-01', '2020-08-11');
Map.addLayer(co.mean().clip(geometry), band_viz, 'S5P CO',0);
var no2 = ee.ImageCollection('COPERNICUS/S5P/NRTI/L3_NO2').select('NO2_column_number_density').filterDate('2020-08-01', '2020-08-11');
Map.addLayer(no2.mean().clip(geometry), band_viz, 'S5P N02',0);
var so = ee.ImageCollection('COPERNICUS/S5P/OFFL/L3_SO2').select('SO2_column_number_density').filterDate('2020-08-01', '2020-08-11');
Map.addLayer(so.mean().clip(geometry), band_viz, 'S5P SO2',0);
var data = ee.ImageCollection('ECMWF/CAMS/NRT').filter(ee.Filter.date('2019-01-01', '2019-01-31'));
var aod = data.select('total_aerosol_optical_depth_at_550nm_surface');
Map.addLayer(aod, visParams, 'Total Aerosal Optical Depth',0);
Map.setCenter(35.50,33.90, 12);
