var dataset = ee.ImageCollection('LANDSAT/LT05/C02/T1_L2')
    .filterDate('2001-08-05', '2001-08-07')
    .filter(ee.Filter.eq('WRS_PATH', 188))
    .filter(ee.Filter.eq('WRS_ROW', 34));

function applyScaleFactors(image) {
  var opticalBands = image.select('SR_B.').multiply(0.0000275).add(-0.2);
  var thermalBand = image.select('ST_B6').multiply(0.00341802).add(149.0);
  return image.addBands(opticalBands, null, true)
              .addBands(thermalBand, null, true);
}

dataset = dataset.map(applyScaleFactors);

var visualization = {
  bands: ['SR_B5', 'SR_B4', 'SR_B3'],
  min: 0.0,
  max: 0.3,
};

Map.addLayer(dataset, visualization, 'False Color (543)');

var visualization2 = {
bands: ['ST_B6'],
min: 280,
max: 340,
};

Map.addLayer(dataset, visualization2, 'Thermal (ST_B6)');

var roi = ee.Geometry.Point([15.0, 37.7]).buffer(20000);
var hotspots = dataset.map(function(img) {
var mask1 = img.select('ST_B6').gte(329);
return img.updateMask(mask1);
});
var visualization3 = {
bands: ['ST_B6'],
min: 329,
max: 360,
palette: ['yellow', 'orange', 'red'],
};

Map.addLayer(hotspots, visualization3, 'Hotspots');

Map.setOptions('HYBRID');
Map.setCenter(15.003, 37.70003, 12);
