var imageCollection = ee.ImageCollection("MODIS/006/MCD12Q1");
var table = ee.FeatureCollection("users/2018212274/gansu");

var gansu = ee.FeatureCollection(table);

var mask=function(image,my_type){
  var mask=image.eq(my_type);
  var masked_image=image.updateMask(mask);
  return masked_image;
}

var dataset = ee.ImageCollection('MODIS/006/MCD12Q1').filterDate('2017-01-01','2017-12-31');

var igbpLandCover = dataset.first().select('LC_Type1');

var image_17=mask(igbpLandCover,17)

var countDictionary = image_17.reduceRegion({
  reducer: ee.Reducer.count(),
  geometry: gansu.geometry(),
  scale: 30,
  maxPixels: 1e9
});

print(countDictionary);

var visparam = {
