var table = ee.FeatureCollection("users/chittanap/StudyArea");
var aoi = table;
var dataset = ee.ImageCollection('ESA/CCI/FireCCI/5_1').filterDate('2008-01-01', '2008-05-31');
var burnedArea = dataset.select('BurnDate');
var baVis = {
  min: 1,
  max: 366,
  palette: [
    'ff0000', 'fd4100', 'fb8200', 'f9c400', 'f2ff00', 'b6ff05',
    '7aff0a', '3eff0f', '02ff15', '00ff55', '00ff99', '00ffdd',
    '00ddff', '0098ff', '0052ff', '0210ff', '3a0dfb', '7209f6',
    'a905f1', 'e102ed', 'ff00cc', 'ff0089', 'ff0047', 'ff0004'
  ]
};
var maxBA = burnedArea.max();
var area = ee.FeatureCollection(aoi);
var maxBAClip = maxBA.clip(area);
Map.centerObject(aoi, 8);
Map.addLayer(aoi, {}, 'Study Area');
Map.addLayer(maxBAClip, baVis, 'Burned Area');
Export.image.toDrive({
  image: maxBAClip,
  description: 'Forest Fires',
  scale: 250,
  maxPixels: 2000000000,
  fileFormat: 'GeoTIFF'
});
