var redriver = ee.FeatureCollection('users/Tarefa01_MAPBIOMAS/bahia').geometry();

var imagem = ee.ImageCollection('MODIS/MOD11A2').select('LST_Day_1km');
var imagem = imagem.map(function(img) {
  return img.multiply(0.02).subtract(273.15).copyProperties(img,['system:time_start',
  'system:time_end']); 
});


var collection10year = ee.ImageCollection(imagem.filterDate('2010-01-01', '2010-12-31'));
var clipOC = collection10year.mean().clip(redriver); 


Map.addLayer (clipOC, {'min': 0, 'max': 45, 'palette':"0000ff,32cd32,ffff00,ff8c00,ff0000"},'Caatinga');

////////////////////////////////////////////////////////

var imagem2 = ee.ImageCollection('MODIS/MOD11A2').select('LST_Night_1km');
var imagem2 = imagem2.map(function(img2) {
  return img2.multiply(0.02).subtract(273.15).copyProperties(img2,['system:time_start',
  'system:time_end']); 
});


var collection10year2 = ee.ImageCollection(imagem2.filterDate('2010-01-01', '2010-12-31'));
var clipOC2 = collection10year2.mean().clip(redriver); 


Map.addLayer (clipOC2, {'min': 0, 'max': 45, 'palette':"0000ff,32cd32,ffff00,ff8c00,ff0000"},'Caatinga');
/////////////////////////////////////

var image3 = clipOC.subtract(clipOC2);

Map.addLayer (image3, {'min': 0, 'max': 45, 'palette':"0000ff,32cd32,ffff00,ff8c00,ff0000"},'Bahia');

print(image3);
