// ================== Module1: Environment Setup ==================
// Define input file paths
var gdpPath = "users/yourusername/BTHGDP2020_ProR";
var popPath = "users/yourusername/BTHPOP2020_ProR";
var regionsPath = "users/yourusername/BTH";

// ================== Module2: Load Data ==================
var regions = ee.FeatureCollection(regionsPath);

// ================== Module3: Prepare Raster Data ==================
var gdp = ee.Image(gdpPath);
var pop = ee.Image(popPath);
gdp = gdp.updateMask(gdp.gte(0));
pop = pop.updateMask(pop.gte(0));

// ================== Module4: Regional Calculations ==================
var calculateStats = function(region) {
  var gdpRegion = gdp.clip(region.geometry());
  var popRegion = pop.clip(region.geometry());
  var weightedGdp = gdpRegion.multiply(popRegion);

  var gdpSum = weightedGdp.reduceRegion({
    reducer: ee.Reducer.sum(),
    geometry: region.geometry(),
    scale: 1000,
    maxPixels: 1e8
  }).get('BTHGDP2020_ProR');

  var popSum = popRegion.reduceRegion({
    reducer: ee.Reducer.sum(),
    geometry: region.geometry(),
    scale: 1000,
    maxPixels: 1e8
  }).get('BTHPOP2020_ProR');

  var weightedGdpPerCapita = ee.Number(gdpSum).divide(ee.Number(popSum));
};

regions.evaluate(function(regionsList) {
  regionsList.features.forEach(function(region) {
    calculateStats(ee.Feature(region));
  });
});
