var table = ee.FeatureCollection(table);
var dataset = ee.ImageCollection('IDAHO_EPSCOR/TERRACLIMATE').filter(ee.Filter.date('2016-01-01', '2020-12-31')).filterBounds(table).select(['pr', 'soil']).map(function(image) {
  var pr = image.select('pr');
  var soil = image.select('soil').multiply(0.1);
  var time = ee.Image.constant(image.date().millis()).rename('time').toFloat();
  return pr.addBands(soil).addBands(time).clip(table);
});
print(ui.Chart.image.series({
  imageCollection: dataset.select(['pr', 'soil']),
  region: table,
  reducer: ee.Reducer.pearsonsCorrelation(),
  scale: 4638.3
}));
function calculateTrend(variable) {
  return dataset.select(['time', variable]).reduce(ee.Reducer.linearFit());
}
var prSlope = calculateTrend('pr').select('scale').rename('pr_trend');
var soilSlope = calculateTrend('soil').select('scale').rename('soil_trend');
Map.centerObject(table, 7);
Map.addLayer(prSlope, {}, 'Precipitation Trend');
Map.addLayer(soilSlope, {}, 'Soil Moisture Trend');
