var roi = ee.Geometry.Rectangle([longitude1,latitude1,longitude2,latitude2]);

var image_2014 = ee.ImageCollection('LANDSAT/LC08/C01/T1_SR')
  .filterDate('2014-01-01','2014-12-31')
  .filterBounds(roi).first();

var image_2020 = ee.ImageCollection('LANDSAT/LC08/C01/T1_SR')
  .filterDate('2020-01-01','2020-12-31')
  .filterBounds(roi).first();

var ndwi_2014 = image_2014.normalizedDifference(['B3','B5']).rename('NDWI');
var ndwi_2020 = image_2020.normalizedDifference(['B3','B5']).rename('NDWI');

var glacier_2014 = ndwi_2014.gt(0.3).rename('Glacier_2014');
var glacier_2020 = ndwi_2020.gt(0.3).rename('Glacier_2020');

var area_2014 = glacier_2014.multiply(ee.Image.pixelArea()).divide(1e6)
  .reduceRegion(ee.Reducer.sum(), roi, 30);

var change_map = glacier_2020.subtract(glacier_2014);

Export.image.toDrive({
  image: change_map,
  description: 'glacier_change_map',
  scale: 30,
  region: roi
});
