var cities = ee.FeatureCollection("users/your_username/cities");
var cities_pop = cities.filter(ee.Filter.notNull(['POP_MAX']));

var bufferSize = 10000;
var densityMap = cities_pop.map(function(city) {
  return city.buffer(bufferSize);
});
var densityRaster = densityMap.reduceToImage(['POP_MAX'], ee.Reducer.sum());

var classifiedCities = cities_pop.map(function(city) {
  var pop = city.get('POP_MAX');
  var cityType = ee.Algorithms.If(pop.lt(50000), 'Small', 
                       ee.Algorithms.If(pop.lt(500000), 'Medium', 'Large'));
  return city.set('city_type', cityType);
});
