var cities = ee.FeatureCollection('USDOS/LSIB_SIMPLE/2017');
var coastline = ee.FeatureCollection('FAO/GAUL/2015/level0');

var citiesPop = cities.map(function(city){
  var latitude = city.geometry().centroid().coordinates().get(1);
  return city.set('latitude', latitude);
}).filter(ee.Filter.notNull(['POP_MAX']));

var nearestCoastlineDistance = citiesPop.map(function(city){
  return city.set('distance_to_coast', coastline.geometry().distance(city.geometry(), 1000));
});
