// ================== Module 1: Data Loading ==================

// 导入全球气候数据（年均气温）
var temperature = ee.ImageCollection("WORLDCLIM/CHIRPS/DAILY/1981_2018")  
                  .filterBounds(ee.Geometry.Rectangle([73.5, 18, 135.05, 53.55]))  
                  .select('temperature');  

// 导入中国边界数据
var china_boundary = ee.FeatureCollection("USDOS/LSIB_SIMPLE/2017")
                        .filter(ee.Filter.eq('country_na', 'China'));

// ================== Module 2: Data Processing ==================

// 获取年均气温图像（此处假设已经是年均气温图像）
var annual_temp = temperature.mean();  

// 裁剪并掩膜数据为中国边界
var temperature_china = annual_temp.clip(china_boundary);

// 分类气温数据为冷、温和、热
var classified_temp = temperature_china
  .where(temperature_china.lt(5), 1)  
  .where(temperature_china.gte(5).and(temperature_china.lt(20)), 2)  
  .where(temperature_china.gte(20), 3);  

// ================== Module 3: Visualization ==================

// 创建地图进行显示
Map.centerObject(china_boundary, 4);  
Map.addLayer(classified_temp, 
  {min: 1, max: 3, palette: ['blue', 'green', 'red']}, 
  'Climate Classification');

// 显示图例
var legend = ui.Panel({
  widgets: [
    ui.Label('Climate Classification'),
    ui.Label('Cold (<5°C)', {color: 'blue'}),
    ui.Label('Mild (5°C-20°C)', {color: 'green'}),
    ui.Label('Hot (>20°C)', {color: 'red'})
  ],
  style: {position: 'bottom-left'}
});
Map.add(legend);
