var temperature = ee.ImageCollection('WORLDCLIM/V1/CLIMATE').filter(ee.Filter.eq('var','bio1')).first();
var cities = ee.FeatureCollection([
  ee.Feature(ee.Geometry.Point([116.4,39.9]),{city:'Beijing'}),
  ee.Feature(ee.Geometry.Point([121.5,31.2]),{city:'Shanghai'}),
  ee.Feature(ee.Geometry.Point([114.1,23.1]),{city:'Guangzhou'})
]);
var buffers = cities.map(function(city){ return city.buffer(100000); });
var temperature_clipped = temperature.clip(buffers);
var meanTempByBuffer = temperature_clipped.reduceRegions({
  collection: buffers,
  reducer: ee.Reducer.mean(),
  scale:1000
});
