var region = ee.FeatureCollection(table);
var dataset = ee.ImageCollection('LANDSAT/LC08/C02/T1_L2')
                .filterBounds(region)
                .filterDate('2015-01-01', '2019-12-31')
                .map(function(img) {
                  return img.clip(region)
                  .addBands(img.expression(
                    '(SWIR - NIR) / (SWIR + NIR)', {
                      'SWIR': img.select('SR_B6'),
                      'NIR': img.select('SR_B5')
                    }).rename('NDBI')
                  );
                });

var urban = dataset.select('NDBI').mean().gt(0.1);
var area = urban.multiply(ee.Image.pixelArea())
              .reduceRegion({
                reducer: ee.Reducer.sum(),
                geometry: region,
                scale: 30
              });
print('Urban Area (km²):', area.get('NDBI').divide(1e6));
