// ================== Module1: Environment Setup ==================
// Set the region of interest (Nanyang region in China)
// You would typically import the relevant shapefile or define the region manually

var nanyangRegion = ee.Geometry.Polygon([[
  [111.65, 32.90],
  [111.65, 33.60],
  [112.60, 33.60],
  [112.60, 32.90]
]]);

// ================== Module2: Load Raster Data ==================
// Upload your GeoTIFF files to Google Earth Engine as assets
// For example, the 2013 land use data would be uploaded and referenced here

var landUse2013 = ee.Image("users/your_username/NanyangLanduse2013R");
var landUse2018 = ee.Image("users/your_username/NanyangLanduse2018R");
var landUse2020 = ee.Image("users/your_username/NanyangLanduse2020R");

// ================== Module3: Resampling Rasters ==================
// GEE automatically handles resampling when performing operations on images
// You can ensure that all images are resampled to the same projection and scale if needed

var resampled2018 = landUse2018.reproject({
  crs: landUse2013.projection(),
  scale: landUse2013.select(0).projection().nominalScale()
});

var resampled2020 = landUse2020.reproject({
  crs: landUse2013.projection(),
  scale: landUse2013.select(0).projection().nominalScale()
});

// ================== Module4: Calculate Land Use Change ==================
// Calculate land use changes (pixel-by-pixel difference)
var change2013to2018 = resampled2018.subtract(landUse2013);
var change2018to2020 = resampled2020.subtract(resampled2018);

// ================== Module5: Visualize Land Use Change ==================
// Function to display change maps using appropriate color map
function visualizeChange(changeImage, title) {
  Map.centerObject(nanyangRegion, 9);
  Map.addLayer(changeImage, {min: -5, max: 5, palette: ['blue', 'white', 'red']}, title);
}

// Visualize land use changes for 2013-2018 and 2018-2020
visualizeChange(change2013to2018, 'Land Use Change (2013-2018)');
visualizeChange(change2018to2020, 'Land Use Change (2018-2020)');
