// 加载美国的边界数据（美国各州边界）
var usStates = ee.FeatureCollection("TIGER/2018/States");

// 示例：加载一个包含各州人口的 FeatureCollection（你需要上传你的数据）
var populationData = ee.FeatureCollection("users/your_user/population_data");

// 将人口数据与州的边界进行合并
var usStatesWithPop = usStates.map(function(feature) {
  var pop = populationData.filter(ee.Filter.equals('state_name', feature.get('name'))).first();
  return feature.set('population', pop.get('total_population'));
});

// 计算每个州的人口密度（单位：人/km²）
usStatesWithPop = usStatesWithPop.map(function(feature) {
  var area = feature.geometry().area().divide(1e6);
  var popDensity = feature.get('population').divide(area);
  return feature.set('pop_density', popDensity);
});

// 对人口密度进行对数变换
usStatesWithPop = usStatesWithPop.map(function(feature) {
  var popDensity = feature.get('pop_density');
  var logPopDensity = ee.Number(popDensity).log10().add(1);
  return feature.set('log_pop_density', logPopDensity);
});

// 可视化对数人口密度
Map.centerObject(usStatesWithPop, 5);
Map.addLayer(usStatesWithPop, {color: 'log_pop_density'}, 'Log Population Density', true);
Map.add(ui.Label('Log Transformed Population Density of US States'));
