// ================== Module 1: Data Retrieval ==================
// Step 1: 获取美国的地形数据（高程数据）
var elevation = ee.Image('USGS/SRTMGL1_003');  // 使用 SRTM 数据集，1秒分辨率

// Step 2: 只保留美国的区域（通过裁剪）
var usa = ee.FeatureCollection('TIGER/2018/States')
           .filter(ee.Filter.eq('STUSPS', 'US'));  // 选取美国（可以根据具体需求调整）
var elevation_usa = elevation.clip(usa);

// ================== Module 2: Data Processing ==================
// Step 3: 数据处理 - 坡度和方位图计算
// 计算坡度
var slope = ee.Terrain.slope(elevation_usa);
// 计算方位
var aspect = ee.Terrain.aspect(elevation_usa);

// ================== Module 3: Visualization of Elevation ==================
// Step 4: 高程数据的可视化
Map.centerObject(usa, 5);
Map.addLayer(elevation_usa, {min: 0, max: 3000, palette: ['blue', 'green', 'brown', 'white']}, 'Elevation');

// ================== Module 4: Terrain Analysis ==================
// Step 5: 坡度和方位数据的可视化
Map.addLayer(slope, {min: 0, max: 60, palette: ['white', 'yellow', 'red']}, 'Slope');
Map.addLayer(aspect, {min: 0, max: 360, palette: ['blue', 'green', 'yellow', 'red']}, 'Aspect');

// ================== Module 5: Visualization of Slope and Aspect ==================
// 通过GEE地图直接查看坡度和方位图
// 可选择导出数据或其他自定义操作，GEE默认提供了直接查看功能
