/**** Start of imports. If edited, may not auto-convert in the playground. ****/
var Bhutan = ee.FeatureCollection("projects/ee07200446droughtratioindex/assets/AOI_Bhutan");
/***** End of imports. If edited, may not auto-convert in the playground. *****/
// Load Sentinel-2 Surface Reflectance data

var s2 = ee.ImageCollection('COPERNICUS/S2_SR')

  .filterBounds(Bhutan)

  .filterDate('2022-01-01', '2023-01-01')

  .filter(ee.Filter.lt('CLOUDY_PIXEL_PERCENTAGE', 5))

  .median()

  .clip(Bhutan).divide(10000);



// Calculate the Drought Ratio Index (DRI)
var ndvi = s2.normalizedDifference(['B8', 'B4']);


  var evi = s2.expression(
    '(2.5 * (nir - red)) / ((nir + 6 * red - 7.5 * blue) + 1)',
    { 'nir': s2.select('B8'),
      'red': s2.select('B4'),
      'blue': s2.select('B2')}  );


var dri = evi.subtract(ndvi).divide(evi.add(ndvi));




// Display the Drought Ratio Index (DRI)
var driVis = {
  min: -1,
  max: 1,
  palette: ['red', 'white', 'blue']};
Map.addLayer(dri, driVis, 'Drought Ratio Index (DRI)');
Map.centerObject(Bhutan)
// Export the Drought Ratio Index (DRI) as a GeoTIFF
Export.image.toDrive({
  image: dri,
  description: 'drought_ratio_index_bhutan',
  scale: 10,
  region:Bhutan,
  maxPixels: 1e13,});
