var Kajiadoboundary = ee.FeatureCollection("users/immakwambo/Kajiadoboundary2018");
var Kajiadoboundary = Kajiadoboundary.geometry();
Map.centerObject(Kajiadoboundary);
Map.addLayer(Kajiadoboundary, {color: 'red'}, 'Kajiadoboundary');

var dataset = ee.ImageCollection('UCSB-CHG/CHIRPS/DAILY')
  .filter(ee.Filter.date('2018-05-01', '2018-05-03'))
  .sort('system:time_start', false);

var latestImage = dataset.first();
var precipitation = latestImage.select('precipitation');

var precipitationVis = {
  min: 1.0,
  max: 17.0,
  palette: ['001137', '0aab1e', 'e7eb05', 'ff4a2d', 'e90000']
};

Map.setCenter(17.93, 7.71, 2);
Map.addLayer(precipitation, precipitationVis, 'Precipitation');

var exportParams = {
  image: precipitation.visualize(precipitationVis),
  description: 'Colorized_Precipitation_Image',
  folder: 'Rainfall',
  scale: 1000,
  region: Kajiadoboundary
};

Export.image.toDrive(exportParams);
