var hatay = ee.FeatureCollection("users/jimmycoates/hatay");

var dataset = ee.ImageCollection("NASA/ORNL/biomass_carbon_density/v1");

var biomass = dataset.select('agb').mean().clip(hatay);

var visualization = {
  bands: ['agb'],
  min: -50.0,
  max: 80.0,
  palette: ['d9f0a3', 'addd8e', '78c679', '41ab5d', '238443', '005a32']
};

Map.addLayer(biomass, visualization, "Aboveground biomass carbon");

Export.image.toDrive({  
  image: biomass,  
  description: 'NASA Above and Below Ground Biomass Carbon Density',  
  scale: 300,  
  region: hatay });

var stats = biomass.reduceRegion({
  reducer: ee.Reducer.mean().combine({
    reducer2: ee.Reducer.stdDev(),
    sharedInputs: true
  }),
  geometry: hatay,
  scale: 300,
  maxPixels: 1e13
});

print('Mean biomass carbon density (Mg/ha):', stats.get('agb_mean'));
print('Standard deviation of biomass carbon density (Mg/ha):', stats.get('agb_stdDev'));
