var image = ee.ImageCollection('LANDSAT/LC08/C02/T1_L2');

    .filterDate('2020-03-22', '2020-06-22');

    .filter.lt('CLOUDY_PIXEL_PERCENTAGE',50);


// Applies scaling factors.
function applyScaleFactors(image) {

  var opticalBands = image.select('SR_B.').multiply(0.0000275).add(-0.2);

  var thermalBands = image.select('ST_B.*').multiply(0.00341802).add(149.0);

  return image.addBands(opticalBands, null, true)
              .addBands(thermalBands, null, true);
}


image = image.map(applyScaleFactors);


var visualization = {
  bands: ['SR_B4', 'SR_B3', 'SR_B2'],
  min: 0.0,
  max: 0.3,
};


Map.addLayer(image, visualization, 'True Color (423)');


Map.setCenter(41.00460419431146,-0.2651923084686196, 8);
