var geometry = ee.Geometry.Polygon([[[-65.16152981392987, -0.7051900433200275], [-65.16152981392987, -17.938910240101567], [-43.935943876429874, -17.938910240101567], [-43.935943876429874, -0.7051900433200275]]], null, false);
var landcover2020 = ee.Image('MODIS/006/MCD12Q1/2020_01_01').select('LC_Type1');
var classe10 = landcover2020.eq(10).multiply(10).rename('10');
var classe12 = landcover2020.eq(12).multiply(12).rename('12');
var classe14 = landcover2020.eq(14).multiply(14).rename('14');
var merge = classe10.add(classe12).add(classe14);
var final_image = merge.reproject('EPSG:4326', null, 500);
Map.addLayer(merge);
Export.image.toDrive({
  image: final_image.clip(geometry),
  description: 'Teste_Giovana_3',
  folder: 'MODIS',
  scale: 500,
  region: geometry,
  fileFormat: 'GeoTIFF'
});
