var dataset = ee.Image("JRC/GSW1_3/GlobalSurfaceWater"),
    table = ee.FeatureCollection("users/latif316/Pakistan"),
    CS = ee.FeatureCollection("users/latif316/Catchment_Shamshal");

var PakistanWater = dataset.select('occurrence').clip(CS);

var visualization = {
  bands: ['occurrence'],
  min: 0.0,
  max: 100.0,
  palette: ['ffffff', 'ffbbbb', '0000ff']
};

Map.centerObject(CS,12)

Map.addLayer(PakistanWater, visualization, 'occurrence');

var flooded_vec = PakistanWater.reduceToVectors({
  scale: 10,
  geometryType:'polygon',
  geometry: CS,
  eightConnected: false,
  bestEffort:true,
  tileScale:2,
});

Export.table.toDrive({
  collection:flooded_vec,
  description:'Shimshal Water Occurrence',
  fileFormat:'SHP',
  fileNamePrefix:'Shimshal Water Occurrence'
});
Export.image.toDrive({
  image:PakistanWater,
  description:'Shimshal Water Occurrence',
  scale: 250,
  fileNamePrefix:'Shimshal Water Occurrence',
  region: CS,
  maxPixels:1e10
});
