var geometry = ee.Geometry.Point([113.60919883968586, 34.6789807148965]);
var pt = ee.Geometry.Point(113.54312181,34.63140607);

var image_before = ee.ImageCollection('COPERNICUS/S2_SR')
  .filterDate('2021-9-20', '2021-9-30')
  .filterBounds(pt)
  .first();

var image_after = ee.ImageCollection('COPERNICUS/S2_SR')
  .filterDate('2021-10-20', '2021-10-25')
  .filterBounds(pt)
  .first();

var image_NBR1 = image_after.normalizedDifference(['B8','B11']);
var image_NBR2 = image_after.normalizedDifference(['B8','B12']);

var visParams1 = {bands: ['B4', 'B3', 'B2'], max: 3048, gamma: 1};
var visParams2 = {bands: ['B12', 'B11', 'B8A'], max: 3048, gamma: 1};

Map.centerObject(pt,13);
Map.addLayer(image_after,visParams1,'after RGB');
Map.addLayer(image_after,visParams2,'after火灾增强 ');
Map.addLayer(image_before,visParams1,'before RGB');
Map.addLayer(image_NBR1,{min:-0.38,max:-0.12,palette:"red,orange,yellow,green,darkgreen"},'image_NBR1');
