var lat = 52.30696;
var lon = 4.48853;
var point = ee.Geometry.Point([lon, lat]);
Map.addLayer(point);

var dataset = ee.ImageCollection('JAXA/GPM_L3/GSMaP/v6/operational')
                  .filter(ee.Filter.date('2021-08-14', '2021-10-21'));
print(dataset);
var precipitation = dataset.select('hourlyPrecipRate');
var precipitationVis = {
  min: 0.0,
  max: 5,
  palette:
      ['1621a2', 'ffffff', '03ffff', '13ff03', 'efff00', 'ffb103', 'ff2300'],
};
Map.setCenter(lon, lat, 2);
Map.addLayer(precipitation, precipitationVis, 'Precipitation');
print(precipitation);
