var roi = ee.FeatureCollection('FAO/GAUL/2015/level1')
             .filter(ee.Filter.eq('ADM1_CODE', '110000'));

var urbanData = ee.Image('THU/UrbanFreq_1978_2017/UrbanFreq_1978_2017_Beijing').select('urban_frequency');

var yearImgs = ee.List.sequence(2000, 2017).map(function(year) {
  return urbanData.gte(year - 1985 + 15)
                  .set('year', year)
                  .clip(roi);
});

var areaStats = ee.ImageCollection(yearImgs).map(function(img) {
  var area = img.multiply(ee.Image.pixelArea())
               .reduceRegion({
                 reducer: ee.Reducer.sum(),
                 geometry: roi,
                 scale: 1000
               });
  return img.set('area_km2', area.get('urban_frequency').divide(1e6));
});

var chart = ui.Chart.feature.byProperty({
  features: areaStats,
  xProperties: ['year'],
  yProperties: ['area_km2']
}).setChartType('LineChart')
 .setOptions({
   title: 'Urban Expansion in Beijing (2000-2017)',
   hAxis: {title: 'Year'},
   vAxis: {title: 'Built-up Area (km²)'}
 });

print(chart);
