// 2011
var pop2011 = ee.ImageCollection("WorldPop/GP/100m/pop")
.filterDate('2011-01-01','2012-01-01')
.filterBounds(Buffer2011).mosaic().clip(Buffer2011)

// Sum as feature
var popsum2011 = pop2011.reduceRegions({
  collection:Buffer2011,
  reducer: ee.Reducer.sum(),
  scale:100
})

// Export
Export.table.toDrive({
  collection:popsum2011,
  description:'popb2011',
  fileFormat:'CSV'
})


