/**** Start of imports. If edited, may not auto-convert in the playground. ****/
var imageCollection = ee.ImageCollection("NOAA/DMSP-OLS/CALIBRATED_LIGHTS_V4"),
    nightlight = ee.ImageCollection("NOAA/DMSP-OLS/CALIBRATED_LIGHTS_V4"),
    table = ee.FeatureCollection("projects/wide-exchanger-220204/assets/tza_boundary"),
    imageCollection2 = ee.ImageCollection("NOAA/VIIRS/DNB/MONTHLY_V1/VCMCFG");
/***** End of imports. If edited, may not auto-convert in the playground. *****/
// set the start and end dates
var start = ee.Date.fromYMD(2017,01,01);
var end = ee.Date.fromYMD(2017,12,31);
var boundary = ee.FeatureCollection("projects/wide-exchanger-220204/assets/tza_boundary");
// clip for the area of interest
// filter for the period of interest
var nightlights2017 = imageCollection2.filterDate(start,end);
// take the mean note that this operation transforms the image collection into an image
nightlights2017 = ee.Image(nightlights2017.mean());

// clip for the area of interest
nightlights2017 = nightlights2017.clip(table);
// select the avg_rad band
nightlights2017 = nightlights2017.select("avg_rad");

Map.addLayer(nightlights2017)

