var MBurn_2001 = ee.ImageCollection("MODIS/006/MCD64A1")

                 .filterBounds(geometry)

                 .filterDate('2001-01-01', '2001-12-31')

                 .select("BurnDate");



// var MBurn_median = MBurn.median();

var MBurn_mean_2001 = MBurn_2001.mean();

var MBurn_mean_r_2001 = MBurn_mean_2001.multiply(0).add(1);

var Burn_2001 = MBurn_mean_r_2001.clip(BOL);



// var reproject

var Burn_2001_prj = Burn_2001

            .reproject('EPSG:4326', null, 500);

Map.addLayer(Burn_2001_prj,{max: 366, min: 1, palette:["46c436","66ff06","f2f518","e4b8bc","eb2a21","722210"] },"2001");



