/**** Start of imports. If edited, may not auto-convert in the playground. ****/
var CHIRPS = ee.ImageCollection("UCSB-CHG/CHIRPS/PENTAD"),
    Countries = ee.FeatureCollection("users/suhas720/india_st");
/***** End of imports. If edited, may not auto-convert in the playground. *****/
// var India = Countries.filter(ee.Filter.eq('country_co', 'IN'));
// print(India)
Map.addLayer(Countries,{},"India");  //to outline India boundaries and highlight
Map.setCenter(78.92, 23.98, 5);  //set center 

var filtered = CHIRPS.filter(ee.Filter.date('2017-01-01', '2018-01-01'))
print(filtered)

var total = filtered.reduce(ee.Reducer.sum()).clip(Countries)  //.sum() is the shorthand code snippet
print(total)

var visParams = {
  min: 0,
  max: 2000,
  palette: ['white','blue'],
}

Map.addLayer(total, visParams, 'Total rainfall')

//Now to calculate average rainfall in India 
var stats = total.reduceRegion({
  reducer: ee.Reducer.mean(),
  geometry: Countries.geometry(),
  scale: 5000
})
print(stats)

Export.image.toDrive({
  image: total,
  description: 'Total_Rainfall_2017',
  scale: 30,
  region: Countries,
  maxPixels: 12142521400
})

Export.table.toDrive({
    collection: filtered,
    description: 'rainfall', 
    folder: 'PN',
    fileNamePrefix: 'Precipitation', 
    fileFormat: 'CSV'
    });
