// ================== 1. 定义路径和输入数据 ==================
// 定义DEM、风速和行政边界的数据路径
var demPath = 'users/yourusername/InnerMongolia_DEM_pro';
var windSpeedPath = 'users/yourusername/InnerMongoliaWind_speed_pro';
var shapefilePath = 'users/yourusername/XilinGol_pro';

// ================== 2. 裁剪栅格数据 ==================
// 裁剪DEM和风速数据
var studyArea = ee.FeatureCollection(shapefilePath);

// 裁剪DEM数据
var clippedDem = ee.Image(demPath).clip(studyArea);

// 裁剪风速数据
var clippedWindSpeed = ee.Image(windSpeedPath).clip(studyArea);

// ================== 3. 计算坡度 ==================
// 计算坡度
var slope = ee.Terrain.slope(clippedDem);

// ================== 4. 加载栅格数据并处理NoData值 ==================
// 在GEE中，不需要像GDAL一样显式处理NoData值
// 你可以直接在计算中排除NoData值
var validDem = clippedDem.updateMask(clippedDem.mask());
var validSlope = slope.updateMask(slope.mask());
var validWindSpeed = clippedWindSpeed.updateMask(clippedWindSpeed.mask());

// ================== 5. 确保栅格形状一致 ==================
// 在GEE中，栅格图层会自动对齐，因此不需要像Python那样检查栅格形状

// ================== 6. 进行适宜性分析 ==================
// 定义适宜性分析条件
var suitability = validDem
  .gte(1000)  // 高度条件
  .and(validDem.lte(2500))
  .and(validSlope.lt(15))  // 坡度条件
  .and(validWindSpeed.gt(4))  // 风速条件
  .rename('suitability');

// ================== 7. 导出适宜性地图 ==================
// 将适宜性地图导出为GeoTIFF
Export.image.toDrive({
  image: suitability,
  description: 'WindFarmSuitability',
  scale: 30,  // 假设数据分辨率是30米
  region: studyArea,
  fileFormat: 'GeoTIFF'
});
