var gfc = ee.Image("UMD/hansen/global_forest_change_2018_v1_6"),
    gtopo30 = ee.Image("USGS/GTOPO30"),
    sample = ee.ImageCollection("users/rspb/birdlife2017_sample"),
    birds = ee.ImageCollection("users/rspb/birdlife2017");

var scale = 1920;
var maxPixels = 1e12;

var forest2000 = gfc.select(['treecover2000']).divide(100);
var areaImage2000 = forest2000.multiply(ee.Image.pixelArea()); 

var allAreas = sample.map(function f(image)
{
  var id = image.get("objectid_1");
  var alt_min = ee.Number(image.get("Min_altitude_m"));
  var alt_max = ee.Number(image.get("Max_altitude_m"));
  var alt_range = gtopo30.gte(alt_min).and(gtopo30.lte(alt_max));
  var aoo = alt_range.and(image);

  var feature = areaImage2000.reduceRegion({
    geometry: aoo.geometry(scale),
    reducer: 'sum',
    scale: scale,
    maxPixels: maxPixels
  }).get('treecover2000');

  return ee.Feature(null, ee.Dictionary({'objectid_1': image.get("objectid_1"), 'sisid': image.get('sisid'), 'tree': feature}));
});

Export.table.toDrive({
  collection: allAreas,
  description: 'Birdlife2017_treecover2000_test',
  folder: 'EarthEngine',
  fileFormat: 'CSV'
});
