task descr: Calculate EVI from Sentinel-2 imagery
// Load a Sentinel-2 SR image collection (most recent)
var image = ee.ImageCollection('COPERNICUS/S3/OLCI')
              .select(['B1', 'B2', 'B3', 'B4', 'B5', 'B6', 'B7', 'B8', 'B9', 'B10', 'B11', 'B12'])
              .first();

// Compute EVI using the formula: G * (B8 - B4) / (B8 + C1*B4 - C2*B2 + L)
var evi = image.expression(
  'G * (NIR - RED) / (NIR + C1 * RED - C2 * BLUE + L)', {
    'NIR' : image.select('B8'),
    'RED' : image.select('B4'),
    'BLUE': image.select('B2'),
    'G'   : 2.5,
    'C1'  : 6.0,
    'C2'  : 7.5,
    'L'   : 1.0
  }).rename('EVI');

// Output the result to the console
print('EVI Image:', evi);
