task descr: Calculate the variance of all pixels in the image
// Load an image (e.g., Landsat 8 surface reflectance)
var image = ee.Image('LANDSAT/LC08/C02/T1_L2').select('SR_B.');

// Calculate the variance of all pixels in the image
var variance = image.reduceRegion({
  reducer: ee.Reducer.variance(),
  geometry: image.geometry(),  // or use a specific area of interest
  scale: 30,                   // spatial resolution of Landsat 8
  maxPixels: 1e10
});

// Output the result
print('Pixel Variance:', variance);
