task descr: Use reduceRegion to calculate the mean NDVI of an area
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load image collection
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterDate('2020-01-01', '2020-12-31')
  .filterBounds(region);

// Map over collection to compute mean NDVI per image
var ndviMean = ndviCol.map(function(img) {
  var mean = img.reduceRegion({
    reducer: ee.Reducer.mean(),
    geometry: region,
    scale: 250
  }).get('NDVI');
  return img.set('NDVI_mean', mean);
});

// Print mean NDVI time series
var chart = ui.Chart.feature.byFeature(ndviMean, 'system:time_start', 'NDVI_mean');
print(chart);
