task descr: Use reduceRegion to calculate the maximum NDVI in the area
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load image collection
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterDate('2020-01-01', '2020-12-31')
  .filterBounds(region);

// Map over collection to compute max NDVI per image
var ndviMax = ndviCol.map(function(img) {
  var max = img.reduceRegion({
    reducer: ee.Reducer.max(),
    geometry: region,
    scale: 250
  }).get('NDVI');
  return img.set('NDVI_max', max);
});

// Print max NDVI time series
var chart = ui.Chart.feature.byFeature(ndviMax, 'system:time_start', 'NDVI_max');
print(chart);
