task descr: Use reduceRegion to calculate the total number of pixels in the area
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load image collection
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterDate('2020-01-01', '2020-12-31')
  .filterBounds(region);

// Map over collection to compute pixel count per image
var pixelCount = ndviCol.map(function(img) {
  var count = img.reduceRegion({
    reducer: ee.Reducer.count(),
    geometry: region,
    scale: 250
  }).get('NDVI');
  return img.set('Pixel_Count', count);
});

// Print pixel count time series
var chart = ui.Chart.feature.byFeature(pixelCount, 'system:time_start', 'Pixel_Count');
print(chart);
