task descr: Calculate the minimum value composite image of the image collection
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load NDVI image collection
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterBounds(region);

// Min composite
var ndviMinImage = ndviCol.min().clip(region);
Map.addLayer(ndviMinImage, {min: 0, max: 9000, palette: ['white', 'brown']}, 'NDVI Min');
