task descr: Calculate the median composite image of an image collection
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load NDVI image collection
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterBounds(region);

// Median composite
var ndviMedianImage = ndviCol.median().clip(region);
Map.addLayer(ndviMedianImage, {min: 0, max: 9000, palette: ['white', 'lightgreen']}, 'NDVI Median');
