task descr: Composite the image set into an RGB layer
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load Landsat collection
var landsatCol = ee.ImageCollection('LANDSAT/LC08/C01/T1_SR')
  .select(['B4', 'B3', 'B2'])
  .filterBounds(region);

// Median composite as RGB
var rgbComposite = landsatCol.median().clip(region);
Map.addLayer(rgbComposite, {min: 0, max: 3000}, 'RGB Composite');
