task descr: Print the metadata of the image collection
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load NDVI image collection
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterBounds(region);

// Map to convert images to FeatureCollection with metadata
var metaFC = ndviCol.map(function(img) {
  return ee.Feature(null, {
    'system:index': img.get('system:index'),
    'system:time_start': img.get('system:time_start'),
    'system:time_end': img.get('system:time_end'),
    'projection': img.projection().crs()
  });
});

// Print metadata FeatureCollection
print('Image collection metadata:', metaFC);
