task descr: Extract image capture date from the image collection
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load NDVI image collection
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterBounds(region);

// Map to extract dates
var dates = ndviCol.map(function(img) {
  return ee.Feature(null, {
    'date': img.date().format('YYYY-MM-dd')
  });
});

// Print dates FeatureCollection
print('Image dates:', dates);
