task descr: Print the cloud cover of each image in the image collection
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load Landsat collection
var landsatCol = ee.ImageCollection('LANDSAT/LC08/C01/T1_SR')
  .filterBounds(region);

// Map to extract cloud cover
var cloudInfo = landsatCol.map(function(img) {
  return ee.Feature(null, {
    'system:index': img.get('system:index'),
    'CLOUD_COVER': img.get('CLOUD_COVER')
  });
});

print('Cloud cover of images:', cloudInfo);
