task descr: Calculate elevation histogram
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003').clip(region);

// Chart elevation histogram
var histogram = ui.Chart.image.histogram({
  image: dem,
  region: region,
  scale: 30
});
print(histogram);
