task descr: Calculate the area of the region facing south.
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003').clip(region);

// Compute aspect
var aspect = ee.Terrain.aspect(dem);

// Mask aspect between 135 and 225 (south-facing)
var southAspectMask = aspect.gte(135).and(aspect.lte(225));

// Compute area
var area = southAspectMask.multiply(ee.Image.pixelArea())
  .reduceRegion({
    reducer: ee.Reducer.sum(),
    geometry: region,
    scale: 30,
    maxPixels: 1e9
  });

print('South-facing area (m²):', area);
