task descr: Calculate the maximum elevation of the DEM
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003').clip(region);

// Compute max elevation
var maxElevation = dem.reduceRegion({
  reducer: ee.Reducer.max(),
  geometry: region,
  scale: 30,
  maxPixels: 1e9
});

print('Max elevation:', maxElevation);
