// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003');

// Compute 2%-98% percentiles
var stats = dem.reduceRegion({
  reducer: ee.Reducer.percentile([2, 98]),
  geometry: dem.geometry(),
  scale: 90,
  maxPixels: 1e9
});

// Get min and max
var min = stats.get('elevation_p2');
var max = stats.get('elevation_p98');

// Display DEM with dynamic stretching
Map.centerObject(dem, 5);
Map.addLayer(dem, {
  min: min,
  max: max,
  palette: ['0000ff', '00ffff', '00ff00', 'ffff00', 'ff0000', 'ffffff']
}, 'DEM dynamic stretch');
