task descr: Show DEM layer with hillshade
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003').clip(region);

// Compute hillshade
var hillshade = ee.Terrain.hillshade(dem);

// Add DEM and hillshade layers
Map.addLayer(dem, {min: 0, max: 3000, palette: ['white', 'green', 'brown']}, 'DEM');
Map.addLayer(hillshade, {min: 0, max: 255, palette: ['black', 'white']}, 'Hillshade');
